Scripts.OnDragStop:
			  if(SmartBuffOptionsFrame:IsVisible()) then
          SMARTBUFF_Splash_ChangePos();
          SMARTBUFF_Splash_ChangeFont(1);
				  self:StopMovingOrSizing();
				end

Scripts.OnClick:
        if (button == "LeftButton" and IsAltKeyDown()) then
          if (IsAddOnLoaded("SmartDebuff")) then
            SMARTDEBUFF_ToggleSF();
          end
				elseif (button == "LeftButton") then
  			  SMARTBUFF_OptionsFrame_Toggle();
				elseif (button == "RightButton") then
				  SMARTBUFF_OToggle();
          if(SmartBuffOptionsFrame:IsVisible()) then
            SMARTBUFF_Options_OnShow();
          end
	      end

Scripts.OnEnter:
				
				GameTooltip:SetOwner(self, "ANCHOR_LEFT");
				GameTooltip:SetText(SMARTBUFF_TITLE);			
        GameTooltip:AddLine(SMARTBUFF_MINIMAP_TT, SMARTBUFF_TTC_R, SMARTBUFF_TTC_G, SMARTBUFF_TTC_B, SMARTBUFF_TTC_A);
        GameTooltip:AppendText("");

Scripts.OnDragStart:
			  
        if (button == "LeftButton" and IsShiftKeyDown()) then
          self.isMoving = true;
        end

Scripts.OnLoad:
        self:EnableMouse(true);
        self:EnableKeyboard(false);      
   	    self:RegisterForDrag("LeftButton");
        self:RegisterForClicks("LeftButtonUp", "RightButtonUp");

        
        
        self:SetScript("PreClick", SMARTBUFF_OnPreClick);
        self:SetScript("PostClick", SMARTBUFF_OnPostClick);
        
        self.title = self:CreateFontString(nil, "OVERLAY");
      	self.title:SetFontObject("GameFontNormal");
      	self.title:SetAllPoints(self);
      	self.title:SetText("SB");
        
        self:SetAttribute("type", "spell");
        self:SetAttribute("unit", nil);
        self:SetAttribute("spell", nil);
        self:SetAttribute("item", nil);
        self:SetAttribute("target-slot", nil);

Scripts.OnDragStart:
if (arg1 == "LeftButton" and IsShiftKeyDown()) then
  self:StartMoving();
end

Scripts.OnValueChanged:
			      local n = self:GetValue();
            getglobal(self:GetName().."Text"):SetText(SMARTBUFF_MSG_CHARGES .. " " .. n);            
            SMARTBUFF_Options.MinCharges = n;
            if (n == 0) then
              SMARTBUFF_Options.CheckCharges = false;
            else
              SMARTBUFF_Options.CheckCharges = true;
            end

local n = self:GetValue();
getglobal(self:GetName().."Text"):SetText(string.format("Splash %.1f sec", n));
SMARTBUFF_Options.SplashDuration = n;

Scripts.OnMouseDown:
if (arg1 == "LeftButton" and IsShiftKeyDown()) then
	self:StartMoving();
end
